mapper.insert:
INSERT 
INTO empyearfreetime( 
    seriesnum
    , employeeid
    , employeename
    , yeartimecode
    , fromdate
    , todate
    , grounds
    , days
    , registred
    , registredtime
    , updated
    , updatedtime
)
VALUES ( 
    #{seriesNum}
    , #{employeeId}
    , #{employeeName}
    , #{yearTimeCode}
    , TO_DATE(#{fromDate}, 'yyyy/mm/dd')
    , TO_DATE(#{toDate}, 'yyyy/mm/dd')
    , #{grounds}
    , TO_DATE(#{toDate}, 'yyyy/mm/dd') - TO_DATE(#{fromDate}, 'yyyy/mm/dd')
    , #{registred}
    , CURRENT_DATE
    , #{updated}
    , #{updatedTime}
)

mapper.select:
SELECT emp.*, com.code, com.codename FROM empyearfreetime emp, commons com WHERE emp.employeeid = #{employeeId} and emp.yeartimecode = com.code
and to_char(emp.fromdate,'yyyy') = #{year}
and to_char(emp.fromdate,'yyyy') = #{year} and to_char(emp.fromdate,'MM') = #{month}
ORDER BY emp.seriesnum desc
